package JavaBasics;

public class LoopConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//while loop , disadv with while loop is if incremental part is not given then infinite loop will arise
		int i=10;
		while(i>=0)
		{
			System.out.println(i);
			i--;
		}
		//for loop
		for(int j=10;j>=0;j--)
		{
			System.out.println(j);
		}
	}

}
